import React, { Component } from 'react';


import {  Link, withRouter } from 'react-router-dom';

import './style.scss';

class Survey extends Component {
    constructor(props){
        super(props)
        this.state = {
            survey: false,
            questions: [],
            duration: ''
        }
    }

    componentDidMount(){
        let user = window.localStorage.getItem('user');
        if(!user)
            this.props.history.push("/");

        this.countDown()
    }

    countDown = () => {
        let minutes = 5;
        let seconds = parseInt(minutes * 60) % 60;

        setInterval(() => {
            seconds--;

            if((seconds % 60) == 0){
                minutes - 1;
            }

            this.setState({duration:
            `${minutes}:${seconds % 60}`})
        }, 1000)
    }

    surveySubmit = (event) => {
        event.preventDefault();
    }

    render(){
        return (
            <div className="survey">
                <h1>Survey ({this.state.duration})</h1>
                <button onClick={() => {
                    this.setState({survey: true})
                }}>Start</button>
                <form onSubmit={(e) => this.surveySubmit(e)} className={!this.state.survey ? 'survey__form hidden' : 'survey__form'}>
                    <label>Question 1 ?</label><br/>
                    <input type="text" name="question1" value={this.state.questions[0]}/> 
                    <br />
                    <label>Question 2 ?</label><br/>
                    <input type="text" name="question2" value={this.state.questions[2]}/> 
                    <br />
                    <label>Question 3 ?</label><br/>
                    <input type="text" name="question3" value={this.state.questions[3]}/> 
                    <br />
                    <label>Question 4 ?</label><br/>
                    <input type="text" name="question4" value={this.state.questions[4]}/> 
                    <br />
                    <label>Question 5 ?</label><br/>
                    <input type="text" name="question5" value={this.state.questions[5]}/> 
                    <br /><br />
                    <button type="submit">
                        Send
                    </button>
                </form> 
            </div>
        )
    }
}


export default withRouter(Survey);
