import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";


import axios from 'axios';


class App extends Component {
  constructor(props){
    super(props)

    this.state = {
      emailLogin: '',
      passwordLogin: '',
      emailCreate: '',
      nameCreate: '',
      lastNameCreate: '',
      passwordCreate: '',
      msg: ''
    }
  } 

  componentDidMount(){

  }

  register = async () => {
    let { emailCreate, nameCreate, lastNameCreate, passwordCreate } = this.state;

    let response = await axios.post('http://localhost:3333/users', {
        "name": nameCreate,
        "email": emailCreate,
        "password": passwordCreate
      }
    );

    if(response.status != 200){
      console.log('Erro ao criar usuário');

      return
    }

    console.log(response);
    console.log('Usuário criado com sucesso!');
  }

  login = async () => {
    let { emailLogin, passwordLogin } = this.state;

    let response = await axios.post('http://localhost:3333/users/login', {
        "email": emailLogin,
        "password": passwordLogin
    });

    if(response.status != 200){
      this.setState({msg: 'Usuário ou senha inválido'})
      return
    }


  }

  render(){
    <Router>
      <div>
        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/about">About</Link>
            </li>
            <li>
              <Link to="/users">Users</Link>
            </li>
          </ul>
        </nav>

        {/* A <Switch> looks through its children <Route>s and
            renders the first one that matches the current URL. */}
        <Switch>
          <Route path="/about">
            <About />
          </Route>
          <Route path="/users">
            <Users />
          </Route>
          <Route path="/">
            <Home />
          </Route>
        </Switch>
      </div>
    </Router>
  }
}

export default App;
